function burger(){try{const t=document.querySelector("#burger-btn"),e=document.querySelector("#menu");t.addEventListener("click",()=>{t.classList.toggle("active"),e.classList.toggle("active")})}catch(t){console.log(t)}}function tabs(){try{const a=document.querySelector(".tab-btns");var c=document.querySelector(".tab-contents");a.addEventListener("click",e=>{if("LI"===e.target.tagName){Array.from(a.children).forEach(t=>t.classList.remove("tab_active")),Array.from(c.children).forEach(t=>t.classList.add("hidden")),e.target.classList.add("tab_active");const t=Array.from(c.children).find(t=>t.dataset.tab===e.target.dataset.tab);t.classList.toggle("hidden")}})}catch(t){console.log(t)}}function accordion(){try{const c=document.querySelectorAll(".accordion-btn");c.forEach(e=>{e.addEventListener("click",()=>{const t=e.querySelector("svg");e.classList.contains("active")?(e.classList.toggle("active"),t.classList.toggle("accordion-btn_active"),e.nextElementSibling.style.maxHeight="0px"):(c.forEach(t=>{const e=t.querySelector("svg");t.nextElementSibling.style.maxHeight="0px",t.classList.remove("active"),e.classList.remove("accordion-btn_active")}),e.classList.add("active"),t.classList.add("accordion-btn_active"),e.nextElementSibling.style.maxHeight=e.nextElementSibling.scrollHeight+40+"px")})})}catch(t){console.log(t)}}console.log("test"),burger(),tabs(),accordion();